// ModIdeaGroupBy is the group-by builder for ModIdea entities.
type ModIdeaGroupBy struct {
	selector
	build *ModIdeaQuery
}

// Aggregate adds the given aggregation functions to the group-by query.
func (migb *ModIdeaGroupBy) Aggregate(fns ...AggregateFunc) *ModIdeaGroupBy {
	migb.fns = append(migb.fns, fns...)
	return migb
}

// Scan applies the selector query and scans the result into the given value.
func (migb *ModIdeaGroupBy) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, migb.build.ctx, ent.OpQueryGroupBy)
	if err := migb.build.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*ModIdeaQuery, *ModIdeaGroupBy](ctx, migb.build, migb, migb.build.inters, v)
}

func (migb *ModIdeaGroupBy) sqlScan(ctx context.Context, root *ModIdeaQuery, v any) error {
	selector := root.sqlQuery(ctx).Select()
	aggregation := make([]string, 0, len(migb.fns))
	for _, fn := range migb.fns {
		aggregation = append(aggregation, fn(selector))
	}
	if len(selector.SelectedColumns()) == 0 {
		columns := make([]string, 0, len(*migb.flds)+len(migb.fns))
		for _, f := range *migb.flds {
			columns = append(columns, selector.C(f))
		}
		columns = append(columns, aggregation...)
		selector.Select(columns...)
	}
	selector.GroupBy(selector.Columns(*migb.flds...)...)
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := migb.build.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

// ModIdeaSelect is the builder for selecting fields of ModIdea entities.
type ModIdeaSelect struct {
	*ModIdeaQuery
	selector
}

// Aggregate adds the given aggregation functions to the selector query.
func (mis *ModIdeaSelect) Aggregate(fns ...AggregateFunc) *ModIdeaSelect {
	mis.fns = append(mis.fns, fns...)
	return mis
}

// Scan applies the selector query and scans the result into the given value.
func (mis *ModIdeaSelect) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, mis.ctx, ent.OpQuerySelect)
	if err := mis.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*ModIdeaQuery, *ModIdeaSelect](ctx, mis.ModIdeaQuery, mis, mis.inters, v)
}

func (mis *ModIdeaSelect) sqlScan(ctx context.Context, root *ModIdeaQuery, v any) error {
	selector := root.sqlQuery(ctx)
	aggregation := make([]string, 0, len(mis.fns))
	for _, fn := range mis.fns {
		aggregation = append(aggregation, fn(selector))
	}
	switch n := len(*mis.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		selector.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		selector.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := mis.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}
