// ServiceItemGroupBy is the group-by builder for ServiceItem entities.
type ServiceItemGroupBy struct {
	selector
	build *ServiceItemQuery
}

// Aggregate adds the given aggregation functions to the group-by query.
func (sigb *ServiceItemGroupBy) Aggregate(fns ...AggregateFunc) *ServiceItemGroupBy {
	sigb.fns = append(sigb.fns, fns...)
	return sigb
}

// Scan applies the selector query and scans the result into the given value.
func (sigb *ServiceItemGroupBy) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, sigb.build.ctx, ent.OpQueryGroupBy)
	if err := sigb.build.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*ServiceItemQuery, *ServiceItemGroupBy](ctx, sigb.build, sigb, sigb.build.inters, v)
}

func (sigb *ServiceItemGroupBy) sqlScan(ctx context.Context, root *ServiceItemQuery, v any) error {
	selector := root.sqlQuery(ctx).Select()
	aggregation := make([]string, 0, len(sigb.fns))
	for _, fn := range sigb.fns {
		aggregation = append(aggregation, fn(selector))
	}
	if len(selector.SelectedColumns()) == 0 {
		columns := make([]string, 0, len(*sigb.flds)+len(sigb.fns))
		for _, f := range *sigb.flds {
			columns = append(columns, selector.C(f))
		}
		columns = append(columns, aggregation...)
		selector.Select(columns...)
	}
	selector.GroupBy(selector.Columns(*sigb.flds...)...)
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := sigb.build.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

// ServiceItemSelect is the builder for selecting fields of ServiceItem entities.
type ServiceItemSelect struct {
	*ServiceItemQuery
	selector
}

// Aggregate adds the given aggregation functions to the selector query.
func (sis *ServiceItemSelect) Aggregate(fns ...AggregateFunc) *ServiceItemSelect {
	sis.fns = append(sis.fns, fns...)
	return sis
}

// Scan applies the selector query and scans the result into the given value.
func (sis *ServiceItemSelect) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, sis.ctx, ent.OpQuerySelect)
	if err := sis.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*ServiceItemQuery, *ServiceItemSelect](ctx, sis.ServiceItemQuery, sis, sis.inters, v)
}

func (sis *ServiceItemSelect) sqlScan(ctx context.Context, root *ServiceItemQuery, v any) error {
	selector := root.sqlQuery(ctx)
	aggregation := make([]string, 0, len(sis.fns))
	for _, fn := range sis.fns {
		aggregation = append(aggregation, fn(selector))
	}
	switch n := len(*sis.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		selector.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		selector.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := sis.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}
